const express = require('express')
const { spawn } = require('child_process')
const get_graph = '../svm_scripts/get_svm_plot.py'
const get_optimized_graph = '../svm_scripts/get_optimized_svm_plot.py'

export function print_graph(state){
    const x1_label = state.x_var; 
    const x2_label = state.y_var;
    const degree = state.degree; 
    const cost = state.cost;
    app.get('/', (req, res) => {

        const { spawn } = require('child_process');
        const py_script = spawn('python', [get_graph, x1_label, x2_label, degree, cost]);
        /*
        py_script.stdout.on('data', function(data) {
    
