import React from 'react';

export default class select_box extends React.Component {
    constructor(props){
        super();
        this.state = {value: props.variable_name};
        this.handleChange = this.handleChange.bind(this);
    }
    
    handleChange(event){
        var id = this.props.id;
        var new_value = event.target.value;
        this.setState({value: new_value}, ()=>{
            this.props.onChange(id, new_value);
        });
    }

    render() {
        return(
            <div class="select-box">
                <div class="select-box__current" tabindex="1">
                    <div class="select-box__value">
                    <input class="select-box__input" id="0" type="radio" value="1" name="Ben" checked="checked" />
                        <p class="select-box__input-text">Cream</p>
                    </div>
                    <div class="select-box__value">
                    <input class="select-box__input" id="1" type="radio" value="2" name="Ben" checked="checked" />
                        <p class="select-box__input-text">Cheese</p>
                    </div>
                    <div class="select-box__value">
                    <input class="select-box__input" id="2" type="radio" value="3" name="Ben" checked="checked" />
                        <p class="select-box__input-text">Milk</p>
                    </div>
                    <div class="select-box__value">
                    <input class="select-box__input" id="3" type="radio" value="4" name="Ben" checked="checked" />
                        <p class="select-box__input-text">Honey</p>
                    </div>
                    <div class="select-box__value">
                    <input class="select-box__input" id="4" type="radio" value="5" name="Ben" checked="checked" />
                        <p class="select-box__input-text">Toast</p>
                    </div><img class="select-box__icon" src="http://cdn.onlinewebfonts.com/svg/img_295694.svg" alt="Arrow Icon" aria-hidden="true" /></div>
                <ul class="select-box__list">
                    <li><label class="select-box__option" for="0" aria-hidden="aria-hidden">Cream</label></li>
                    <li><label class="select-box__option" for="1" aria-hidden="aria-hidden">Cheese</label></li>
                    <li><label class="select-box__option" for="2" aria-hidden="aria-hidden">Milk</label></li>
                    <li><label class="select-box__option" for="3" aria-hidden="aria-hidden">Honey</label></li>
                    <li><label class="select-box__option" for="4" aria-hidden="aria-hidden">Toast</label></li>
                </ul>
            </div>
            /*
            <div className='selector-container'>
                <select value={this.state.value} onChange={this.handleChange}>
                    <option value='mean concavity'>Mean Concavity</option>
                    <option value='mean texture'>Mean Texture</option>
                    <option value='mean compactness'>Mean Compactness</option>
                    <option value='worst concavity'>Worst Concavity</option>
                    <option value='worst texture'>Worst Texture</option>
                    <option value='worst compactness'>Worst Compactness</option>
                </select>
            </div>
            */
        )
    }
}
